mapper.select:
select count(1) from role_resource
where
resource_id=#{resourceId,jdbcType=BIGINT}
and operate=#{operate,jdbcType=VARCHAR}
and role_id =
#{roleId,jdbcType=BIGINT}

select count(1) from role_resource inner join
resource on resource.id = role_resource.resource_id
where
resource.code=#{resourceCode,jdbcType=VARCHAR}
and
operate=#{operate,jdbcType=VARCHAR}
and role_id =
#{roleId,jdbcType=BIGINT}

mapper.delete:
delete role_resource
where resource_id=#{resourceId,jdbcType=BIGINT}
and
operate=#{operate,jdbcType=VARCHAR}
and role_id = #{roleId,jdbcType=BIGINT}
