mapper.update:
update account
set
account_enable=#{enable,jdbcType=BIT}
where account_id =
#{id,jdbcType=BIGINT}

mapper.select:
select account_openId
from account where account_id =
#{id,jdbcType=BIGINT}

select

from account

	
		group_id=#{groupId,jdbcType=BIGINT}
	
	
		and account_name=#{name,jdbcType=VARCHAR}
	
	
		and account_openId=#{openId,jdbcType=VARCHAR}
	
	
		and account_password=#{password,jdbcType=VARCHAR}
	
	
		and role_id=#{roleId,jdbcType=BIGINT},
	
	and account_enable=#{enable,jdbcType=BIT}
